import React, { useState } from "react";

const ErrorModal = ({ title, message, errorState }) => {
  const closeModal = () => {
    errorState(false);
  };

  return (
    <div>
      <h1>{title}</h1>
      <p>{message}</p>

      <button onClick={closeModal}>Okay</button>
    </div>
  );
};

export default ErrorModal;
