import React, { useState } from "react";
import AddUser from "./components/Users/AddUser";
import UsersList from "./components/Users/UsersList";
import data from "../src/components/Data/data";

// 첫 번쨰 : 더미 데이터를 만든다
// 두 번쨰 : 데이터 출력을 확인한다
// 세 번쨰 : 컴포넌트의 목적 생각하기
// - AddUser : 유저 생성
// - UserList : 유저 배치
// 네 번쨰 : App에서 내려줄 prop 생각하기
// State 끌어올리기

const App = () => {
  const [userItem, setUserItem] = useState([data]);

  return (
    <>
      <AddUser></AddUser>
      <UsersList datas={userItem}></UsersList>
    </>
  );
};

export default App;
